#define QPOW_DATA_TYPE int
const int MOD=1000;QPOW_DATA_TYPE qpow(QPOW_DATA_TYPE base,QPOW_DATA_TYPE exponential){QPOW_DATA_TYPE res=1;while(exponential){if(exponential&1)res=res*base;base=base*base;exponential>>=1;}return res;}QPOW_DATA_TYPE qpow_MOD(QPOW_DATA_TYPE base,QPOW_DATA_TYPE exponential){QPOW_DATA_TYPE res=1;base%=MOD;while(exponential){if(exponential&1)res=(res*base)%MOD;base=(base*base)%MOD;exponential>>=1;}return res%MOD;}QPOW_DATA_TYPE qpow_dg_MOD(const QPOW_DATA_TYPE&a,QPOW_DATA_TYPE n){if(n==0)return 1;if(n%2==1){return qpow_dg_MOD(a,n-1)*a%MOD;}int temp=qpow_dg_MOD(a,n/2)%MOD;return temp*temp%MOD;}int qpow_dg_MOD(const QPOW_DATA_TYPE&a,QPOW_DATA_TYPE n){if(n==0)return 1;if(n%2==1){return qpow_dg_MOD(a,n-1)*a;}int temp=qpow_dg_MOD(a,n/2);return temp*temp;}